mapper.select:
select 
from tb_user
where login_name = #{loginNameOrId} or id = #{loginNameOrId}

mapper.delete:
delete from tb_user where id in (
#{id}
)

  and user_type = #{userType}

mapper.update:
update tb_user


        login_name = #{user.loginName},


        user_name = #{user.userName},


        passwd = #{user.passwd},


where id = #{user.id}

  and user_type = #{user.userType}
